import React from "react";
import AddRandomContact from "./AddRandomContact";
import RemoveAllContact from "./RemoveAllContact";
import AddContact from "./AddContact";
import Footer from "../Layout/Footer";
import Header from "../Layout/Header";
import GeneralContacts from "./GeneralContacts";

import FovoriteContacts from "./FavoriteContacts";

class ContactIndex extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      contactList: [
        {
          id: 1,
          name: "dimitris leve",
          phone: "123456789",
          email: "Alexkr21@dotnet.com",
          isFavorite: false,
        },
        {
          id: 2,
          name: "Nikos Ampatzis",
          phone: "123456781",
          email: "NikosAmp@dotnet.com",
          isFavorite: true,
        },
        {
          id: 3,
          name: "Tax Klouras",
          phone: "123456782",
          email: "TaxKlouras@dotnet.com",
          isFavorite: true,
        },
      ],
    };
  }
  handleAddContact = (newContact) => {
    const newFinalConstact = {
      ...newContact,
      id: this.state.contactList[this.state.contactList - 1].id + 1,
      isFavorite: false,
    };
    this.setState((prevState) => {
      return {
        contactList: prevState.contactList.concat([newFinalConstact]),
      };
    });
    alert("hello");
  };

  render() {
    return (
      <div>
        <Header />
        <div className="container" style={{ minHeight: "85vh" }}>
          <div className="row py-3">
            <div className="col-4 offset-2">
              <AddRandomContact />
            </div>
            <div className="col-2">
              <RemoveAllContact />
            </div>
            <div className="row py-2">
              <div className="col-8 offset-2 row">
                <AddContact handleAddContact={this.handleAddContact} />
              </div>
            </div>
            <div className="row py-2">
              <div className="col-8 offset-2 row">
                <FovoriteContacts
                  contacts={this.state.contactList.filter(
                    (u) => u.isFavorite == true
                  )}
                />
              </div>
            </div>
            <div className="row py-2">
              <div className="col-8 offset-2 row">
                <GeneralContacts
                  contacts={this.state.contactList.filter(
                    (u) => u.isFavorite == false
                  )}
                />
              </div>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}

export default ContactIndex;
