import React from "react";

class AddContact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      errorMessage: undefined,
      successMessage: undefined,
    };
  }

  handleAddContactFormSubmit = (e) => {
    e.preventDefault();
    const name = e.target.elements.contactName.value.trim();
    const email = e.target.elements.contactEmail.value.trim();
    const phone = e.target.elements.contactPhone.value.trim();
    const response = this.props.handleAddContact({
      name: name,
      email: email,
      phone: phone,
    });

    if (response.status === "success") {
      this.setState({ errorMessage: undefined, successMessage: response.msg });
      document.querySelector(".contact-form").reset();
    } else {
      this.setState({ errorMessage: response.msg, successMessage: undefined });
    }
  };

  render() {
    return (
      <div className="border p-4 bg-dark text-white rounded">
        <form onSubmit={this.handleAddContactFormSubmit} className="contact-form">
          <h3 className="text-center mb-4">Add a new Contact</h3>
          <div className="mb-3">
            <input
              className="form-control"
              placeholder="Name..."
              name="contactName"
            />
          </div>
          <div className="mb-3">
            <input
              className="form-control"
              placeholder="Email..."
              name="contactEmail"
            />
          </div>
          <div className="mb-3">
            <input
              className="form-control"
              placeholder="Phone..."
              name="contactPhone"
            />
          </div>
          {this.state.errorMessage && (
            <div className="alert alert-danger text-center">{this.state.errorMessage}</div>
          )}
          {this.state.successMessage && (
            <div className="alert alert-success text-center">{this.state.successMessage}</div>
          )}
          <div className="text-center">
            <button className="btn btn-primary">Create</button>
          </div>
        </form>
      </div>
    );
  }
}

export default AddContact;
