import React from "react";
import AddRandomContact from "./AddRandomContact";
import RemoveAllContact from "./RemoveAllContact";
import AddContact from "./AddContact";
import Footer from "../Layout/Footer";
import Header from "../Layout/Header";
import GeneralContacts from "./GeneralContacts";
import FavoriteContacts from "./FavoriteContacts";

class ContactIndex extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      contactList: [
        {
          id: 1,
          name: "dimitris leve",
          phone: "123456789",
          email: "Alexkr21@dotnet.com",
          isFavorite: false,
        },
        {
          id: 2,
          name: "Nikos Ampatzis",
          phone: "123456781",
          email: "NikosAmp@dotnet.com",
          isFavorite: true,
        },
        {
          id: 3,
          name: "Tax Klouras",
          phone: "123456782",
          email: "TaxKlouras@dotnet.com",
          isFavorite: true,
        },
      ],
      selectedContact: undefined,
      isUpdating: false,
    };
  }

  handleAddContact = (newContact) => {
    if (!newContact.name) {
      return { status: "failure", msg: "Please enter a valid name" };
    } else if (!newContact.phone) {
      return { status: "failure", msg: "Please enter a valid phone" };
    }

    const duplicateRecord = this.state.contactList.some(
      (contact) =>
        contact.name === newContact.name && contact.phone === newContact.phone
    );

    if (duplicateRecord) {
      return { status: "failure", msg: "Duplicate record" };
    } else {
      const newFinalContact = {
        ...newContact,
        id: this.state.contactList[this.state.contactList.length - 1].id + 1,
        isFavorite: false,
      };
      this.setState((prevState) => ({
        contactList: [...prevState.contactList, newFinalContact],
      }));
      return { status: "success", msg: "Contact added successfully" };
    }
  };

  handleUpdateContact = (updatedContact) => {
    if (!updatedContact.name) {
      return { status: "failure", msg: "Please enter a valid name" };
    } else if (!updatedContact.phone) {
      return { status: "failure", msg: "Please enter a valid phone" };
    }

    this.setState((prevState) => ({
      contactList: prevState.contactList.map((contact) =>
        contact.id === updatedContact.id
          ? { ...contact, ...updatedContact }
          : contact
      ),
      isUpdating: false,
      selectedContact: undefined,
    }));

    return { status: "success", msg: "Contact updated successfully" };
  };

  handleToggleFavorites = (contactId) => {
    this.setState((prevState) => ({
      contactList: prevState.contactList.map((contact) =>
        contact.id === contactId
          ? { ...contact, isFavorite: !contact.isFavorite }
          : contact
      ),
    }));
  };

  handleRemoveContact = (contactId) => {
    this.setState((prevState) => ({
      contactList: prevState.contactList.filter(
        (contact) => contact.id !== contactId
      ),
    }));
  };

  handleAddRandomContact = (newContact) => {
    const newFinalContact = {
      ...newContact,
      id: this.state.contactList[this.state.contactList.length - 1].id + 1,
      isFavorite: false,
    };
    this.setState((prevState) => ({
      contactList: [...prevState.contactList, newFinalContact],
    }));
  };

  handleRemoveAllContacts = () => {
    this.setState({ contactList: [] });
  };

  handleUpdateClick = (contact) => {
    this.setState({ selectedContact: contact, isUpdating: true });
  };

  cancelUpdateContact = () => {
    this.setState({ selectedContact: undefined, isUpdating: false });
  };

  render() {
    const { contactList, selectedContact, isUpdating } = this.state;

    return (
      <div>
        <Header />
        <div className="container py-4" style={{ minHeight: "85vh" }}>
          <div className="row mb-3">
            <div className="col-md-6">
              <AddRandomContact handleAddRandomContact={this.handleAddRandomContact} />
            </div>
            <div className="col-md-6 text-md-end">
              <RemoveAllContact handleRemoveAllContacts={this.handleRemoveAllContacts} />
            </div>
          </div>

          <div className="row mb-3">
            <div className="col-12">
              <AddContact
                handleAddContact={this.handleAddContact}
                isUpdating={isUpdating}
                selectedContact={selectedContact}
                cancelUpdateContact={this.cancelUpdateContact}
                handleUpdateContact={this.handleUpdateContact}
              />
            </div>
          </div>

          <div className="row">
            <div className="col-12 mb-3">
              <FavoriteContacts
                contacts={contactList.filter((contact) => contact.isFavorite)}
                favoriteClick={this.handleToggleFavorites}
                deleteContact={this.handleRemoveContact}
                updateClick={this.handleUpdateClick}
              />
            </div>
            <div className="col-12">
              <GeneralContacts
                contacts={contactList.filter((contact) => !contact.isFavorite)}
                favoriteClick={this.handleToggleFavorites}
                deleteContact={this.handleRemoveContact}
                updateClick={this.handleUpdateClick}
              />
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}

export default ContactIndex;
